import React, { useEffect, useState } from 'react'
import axios from 'axios'

function Test1()
{
    const [data, setdata] = useState([])
    const [show , setshow] = useState([])
    useEffect( () => {
        axios.get("https://api.npoint.io/93bed93a99df4c91044e")
        .then(res => {
            console.log(res.data.body.Recommendations)
            setdata(res.data.body.Recommendations)
            
        })
        .catch(err => 
            {
                return err.meassage
            })
    },[])
    
    const showItem = (username) => {
        const showstate = show.slice();
        const index = showstate.indexOf(username)

        if(index >=0 )
        {
            showstate.splice(index,1);
            setshow(showstate)
        }
        else{
            showstate.push(username)
            setshow(showstate)
        }
    }


    
    return (
        <div>
        {
            data.map((post) => {
                return (    
                    <div>
                        <p>{post.RestaurantName}</p> 
                        <button onClick={() =>showItem(post.RestaurantName)}>To</button>
                        {
                            show.includes(post.RestaurantName) && post.menu.map((menu) =>{
                                if(menu.type == "sectionheader")
                                {
                                    return (
                                        <div>
                                            <p>----{menu.name}</p>
                                            <button onClick={() =>showItem(post.RestaurantName)}>To</button>
                                        </div>
                                        
                                    )
                                }})
                        }
                    </div>
                )
            })
        }
    </div>
    )
}
export default Test1
